var path=require('path');
var fs=require('fs');
exports.getType=function(target_url,cb){
	var extname=path.extname(target_url);//取得后缀名
	fs.readFile('./node_modules/netEx/mime.json', function(err,data){
		if(err){
			console.log(err);
			return false;
		}
		var data=JSON.parse(data.toString());
		// console.log(data[extname]);// 访问json
		cb(data[extname]);//异步处理低速函数
	});
	
}